import {createStore} from 'vuex'

export default createStore({
  state() {
    return {
      tasks: [
        {
          id: 1,
          text: 'Закончить проект',
          completed: false,
          subtasks: [
            {id: 1, text: "Создать компоненты", completed: true},
            {id: 2, text: "Написать тесты", completed: false}
          ],
          add_subtask: ''
        },
        {
          id: 2,
          text: 'Купить продукты',
          completed: true,
          subtasks: [
            {id: 1, text: "Хлеб", completed: true},
            {id: 2, text: "Молоко", completed: false},
            {id: 3, text: "Яйца", completed: true}
          ],
          add_subtask: ''
        }
      ],
      add_task: '',
      filter: 'all'
    }
  },
  getters: {
    showTasks: (state) => {
      return  state.tasks
    },
    // showSubtask: (state) => {}
  },
  mutations: {
    ADD_TASK(state, taskText) {
      const newTask = {
        id: Date.now(),
        text: taskText.trim(),
        completed: false,
        subtasks: []
      }

      // Проверка на пустую строку
      if (newTask.text) {
        state.tasks.push(newTask)
      }
    },
    ADD_SUBTASK(state, {taskId, subtaskText}) {
      console.log(subtaskText)
      const newSubtask = {
        id: Date.now(),
        text: subtaskText.trim(),
        completed: false,
      }

      let currentTask = state.tasks.find(task => task.id === taskId)
      if (newSubtask.text) {
        currentTask.subtasks.push(newSubtask)
      }
    },
    DELETE_TASK(state, taskId) {
      state.tasks.splice(state.tasks.indexOf(taskId), 1)
    },
    DELETE_SUBTASK(state, {taskId, subtaskId}) {
      const taskIndex = state.tasks.findIndex(task => task.id === taskId);
      const subs = state.tasks[taskIndex].subtasks;
      const subIndex = subs.findIndex(s => s.id === subtaskId);
      subs.splice(subIndex, 1);
    },
    UPDATE_TASK_CHECKED(state, taskId) {
      const task = state.tasks.find(t => t.id === taskId);
      task.checked = !task.checked;
    },
    UPDATE_SUBTASK_CHECKED(state, {taskId, subtaskId}) {
      const task = state.tasks.find(task => task.id === taskId);
      const subtask = task.subtasks.find(t => t.id === subtaskId)
      subtask.checked = !subtask.checked;
    },
    UPDATE_TASK_CHECKED_state() {

    },
    UPDATE_SUBTASK_CHECKED_state() {

    },
  }
})
