import {createStore} from 'vuex'

export default createStore({
  state() {
    return {
      tasks: [
        {
          id: 1,
          text: 'Закончить проект',
          completed: false,

          add_subtask: ''
        },
        {
          id: 2,
          text: 'Купить продукты',
          completed: true,
          subtasks: [
            {id: 1, text: "Хлеб", completed: true},
            {id: 2, text: "Молоко", completed: false},
            {id: 3, text: "Яйца", completed: true}
          ],
          add_subtask: ''
        }
      ],
      add_task: '',
      filter: 'all'
    }
  },
  getters: {
    // showTask: (state) => {},
    // showSubtask: (state) => {}
  }
})
