import {createStore} from 'vuex'

export default createStore({
  state() {
    return {
      filter: 'all',
      tasks: {
        id: 1,
        text: 'Создайте первую задачу',
        completed: false,
        subtasks: [
          {id: 1, text: "asd", completed: true},
          {id: 2, text: "asd", completed: false}
        ],
        add_subtask: ''
      },
      add_task: '',
    }
  },
  getters: {
    showTask: (state) => {},
    showSubtask: (state) => {}
  }
})
