import { createStore } from 'vuex'



export default createStore({
  state() {
    return {
      tasks: [
        {
          id: 1,
          text: 'Закончить проект',
          completed: false,
          subtasks: [
            { id: 1, text: "Создать компоненты", completed: true },
            { id: 2, text: "Написать тесты", completed: false }
          ]
        },
        {
          id: 2,
          text: 'Купить продукты',
          completed: true,
          subtasks: [
            { id: 1, text: "Хлеб", completed: true },
            { id: 2, text: "Молоко", completed: false },
            { id: 3, text: "Яйца", completed: true }
          ]
        }
      ],
      add_task: '',
      filter: 'all'
    }
  },
  getters: {
    showTasks: (state) => state.tasks,
  },
  mutations: {
    ADD_TASK(state, taskText) {
      const newTask = {
        id: Date.now(),
        text: taskText.trim(),
        completed: false,
        subtasks: []
      };
      if (newTask.text) state.tasks.push(newTask);
    },
    ADD_SUBTASK(state, { taskId, subtaskText }) {
      const newSubtask = {
        id: Date.now(),
        text: subtaskText.trim(),
        completed: false,
      };
      const currentTask = state.tasks.find(task => task.id === taskId);
      if (newSubtask.text && currentTask) {
        currentTask.subtasks.push(newSubtask);
      }
    },
    DELETE_TASK(state, taskId) {
      state.tasks = state.tasks.filter(task => task.id !== taskId);
    },
    DELETE_SUBTASK(state, { taskId, subtaskId }) {
      const task = state.tasks.find(task => task.id === taskId);
      if (task) {
        task.subtasks = task.subtasks.filter(s => s.id !== subtaskId);
      }
    },
    UPDATE_TASK_CHECKED(state, taskId) {
      const task = state.tasks.find(t => t.id === taskId);
      if (task) {
        task.completed = !task.completed;
        update_subtasks_checked_state(state, task);
      }
    },
    UPDATE_SUBTASK_CHECKED(state, { taskId, subtaskId }) {
      const task = state.tasks.find(task => task.id === taskId);
      if (!task) return;
      const subtask = task.subtasks.find(t => t.id === subtaskId);
      if (subtask) subtask.completed = !subtask.completed;
    }
  }
})
